package pack_1;

public class staticvar {
	static int x =10;
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		   staticvar t1 = new staticvar();
		   t1.x=50;
		   System.out.println(t1.x);  
		   
		   staticvar t2 = new staticvar();
		   System.out.println(t2.x); 
	}

}
